import React, {useEffect, useState} from 'react';
import {ISalaryData, SalaryResponse} from "../types/ISalaryData";
import SalariesService from "../services/SalariesService";
import DataTable, {TableColumn} from "react-data-table-component";
import {Divider, Paper, Tooltip, Typography} from "@mui/material";

const SalariesScreen = () => {
    const [salaries, setSalaries] = useState<ISalaryData | null>(null);

    useEffect(() => {
        fetchSalaries();
    }, []);

    const fetchSalaries = () => {
        SalariesService.getAllSalaries()
            .then((response: any) => {
                setSalaries(response.data);
                console.log("CHECK DATA: " + salaries);
            })
            .catch((e: Error) => {
                console.error(e);
            })
    }


    const createTooltipColumn = (
        name: string,
        selector: (row: SalaryResponse) => string
    ): TableColumn<SalaryResponse> => {
        return {
            name: <Typography variant="body1" color="primary">{name}</Typography>,
            cell: (row: SalaryResponse) => (
                <Tooltip title={selector(row)} arrow>
                    <Typography variant="body2" noWrap>
                        {selector(row)}
                    </Typography>
                </Tooltip>
            ),
            sortable: true,
        };
    };

    const columns: TableColumn<SalaryResponse>[] = [
        createTooltipColumn('Name', (row) => row?.employee),
        // {
        //     name: <Typography variant="body1" color="primary">Amount</Typography>,
        //     cell: (row: ISalaryData) => (
        //         <Typography variant="body2" noWrap>
        //             {row.amount} {currencyCode}
        //         </Typography>
        //     ),
        //     sortable: true,
        // },
        // {
        //     name: <Typography variant="body1" color="primary">Recurring</Typography>,
        //     cell: (row: ISalaryData) => (
        //         <Typography variant="body2" noWrap>
        //             {row.recurring ? 'Yes' : 'No'}
        //         </Typography>
        //     ),
        //     sortable: true,
        // },
        // {
        //     name: <Typography variant="body1" color="primary">Recurrence Period</Typography>,
        //     selector: row => row.recurrencePeriod,
        //     sortable: true,
        // },
        // {
        //     name: <Typography variant="body1" color="primary">Date</Typography>,
        //     selector: row => row.date,
        //     cell: (row: ISalaryData) => (
        //         <Typography variant="caption">
        //             {format(new Date(row.date), 'PPP p')}
        //         </Typography>
        //     ),
        //     sortable: true,
        // },
        // {
        //     name: <Typography variant="body1" color="primary">Options</Typography>,
        //     cell: (row: ISalaryData) => (
        //         <Box sx={{mt: 1, mb: 1, display: 'flex', flexDirection: 'column', gap: '8px'}}>
        //             <Button
        //                 variant="contained"
        //                 color="primary"
        //                 onClick={() => handleEdit(row)}
        //                 size="small"
        //                 startIcon={<EditIcon/>}
        //             >
        //                 Edit
        //             </Button>
        //             <Button
        //                 variant="contained"
        //                 color="primary"
        //                 onClick={() => handleDelete(row)}
        //                 size="small"
        //                 startIcon={<DeleteIcon/>}
        //             >
        //                 Delete
        //             </Button>
        //         </Box>
        //     ),
        //     style: {
        //         minWidth: '140px',
        //     },
        // }
    ];
    //
    // console.log("LENGTH: " + salaries[0]?.data.length)
    // console.log("DATA: " + salaries[0]?.data)

    return (
        <>
            <Paper elevation={3}
                   sx={{padding: 3, marginTop: 3, marginLeft: 'auto', marginRight: 'auto'}}>

                <Typography variant="button" display="block" gutterBottom>
                    <Typography component="span" sx={{color: "error.main"}}>
                        {'No salaries added yet.'}
                    </Typography>
                </Typography>

                <Divider/>

                <DataTable
                    key={salaries?.data.length}
                    columns={columns}
                    data={salaries?.data}
                    pagination
                    expandableRows
                    // expandableRowsComponent={ExpandedIncomesDetails}
                />
            </Paper>
        </>
    );
};

export default SalariesScreen;
